mapper.select:
SELECT session_id, user_id, last_msg_id, last_seq, updated_at
FROM session_read_offset
WHERE user_id = #{userId}
  AND session_id IN

    #{sid}

mapper.update:
UPDATE session_read_offset
SET last_seq = #{lastSeq}, updated_at = #{updatedAt}
WHERE session_id = #{sessionId}
  AND user_id = #{userId}
  AND last_seq < #{lastSeq}

mapper.select:
SELECT last_seq
FROM session_read_offset
WHERE session_id = #{sessionId}
  AND user_id = #{userId}
LIMIT 1
