mapper.insert:
INSERT INTO user_detail (
    user_id, last_login_time, last_login_ip,
    rename_times_left, im_id_times_left, avatar_times_left, signature_times_left,
    extension
)
VALUES (
    #{userId}, #{lastLoginTime}, #{lastLoginIp},
    #{renameTimesLeft}, #{imIdTimesLeft}, #{avatarTimesLeft}, #{signatureTimesLeft},
    #{extension}
)

mapper.update:
UPDATE user_detail
last_login_time = #{lastLoginTime}, last_login_ip = #{lastLoginIp}, rename_times_left = #{renameTimesLeft}, im_id_times_left = #{imIdTimesLeft}, avatar_times_left = #{avatarTimesLeft}, signature_times_left = #{signatureTimesLeft}, extension = #{extension},
WHERE user_id = #{userId}
