mapper.insert:
INSERT INTO user_profile (
    user_id, im_id, nick_name, avatar, sex, personal_signature,
    area_name, area_code, avatar_version, nickname_version
)
VALUES (
    #{userId}, #{imId}, #{nickName}, #{avatar}, #{sex}, #{personalSignature},
    #{areaName}, #{areaCode}, #{avatarVersion}, #{nicknameVersion}
)

mapper.update:
UPDATE user_profile
im_id = #{imId}, nick_name = #{nickName}, avatar = #{avatar}, sex = #{sex}, personal_signature = #{personalSignature}, area_name = #{areaName}, area_code = #{areaCode}, avatar_version = #{avatarVersion}, nickname_version = #{nicknameVersion},
WHERE user_id = #{userId}

mapper.select:
SELECT COUNT(1) > 0
FROM user_profile
WHERE im_id = #{imId}
